import React, { useState } from "react";
import { LocateFixed, Circle, MapPin } from "lucide-react";
import axios from "axios";
import debounce from "lodash.debounce";
import * as DropdownMenu from "@radix-ui/react-dropdown-menu";

const LocationForm = ({
  onNext,
  formValues,
  handleChange,
  handleGetDistance,
  getUserLocation,
}) => {
  const [startSuggestions, setStartSuggestions] = useState([]);
  const [restaurantSuggestions, setRestaurantSuggestions] = useState([]);

  const fetchSuggestions = async (query, setSuggestions) => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/autocomplete", {
        params: { input: query },
      });
      setSuggestions(response.data);
    } catch (error) {
      console.error("Error fetching suggestions:", error);
    }
  };

  const debouncedFetchStartSuggestions = debounce((query) => {
    if (query) fetchSuggestions(query, setStartSuggestions);
  }, 300);

  const debouncedFetchRestaurantSuggestions = debounce((query) => {
    if (query) fetchSuggestions(query, setRestaurantSuggestions);
  }, 300);

  const handleStartChange = (e) => {
    handleChange(e);
    debouncedFetchStartSuggestions(e.target.value);
  };

  const handleRestaurantChange = (e) => {
    handleChange(e);
    debouncedFetchRestaurantSuggestions(e.target.value);
  };

  return (
    <form
      onSubmit={handleGetDistance}
      className="w-96 p-6 px-10 rounded-lg bg-white shadow-lg mx-auto space-y-4"
    >
      <h3 className="mb-7">Choose your route</h3>

      <DropdownMenu.Root open={startSuggestions.length > 0}>
        <div className="flex justify-between">
          <label className="p-2 formLabel" htmlFor="start">
            <Circle className="size-4" />
          </label>
          <input
            className="formInput"
            type="text"
            id="start"
            value={formValues.start || ""}
            onChange={handleStartChange}
          />
          <DropdownMenu.Content className="bg-white border border-gray-300 rounded-md shadow-lg z-10 w-80 mt-2">
            <DropdownMenu.Item
              className="flex items-center px-3 py-2 cursor-pointer hover:bg-gray-100"
              onClick={() => {
                getUserLocation();
                handleChange({
                  target: { id: "start", value: "Your location" },
                });
              }}
            >
              <LocateFixed className="mr-2 text-blue-600 w-5 h-5" />
              Your location
            </DropdownMenu.Item>
            <DropdownMenu.Separator className="h-px bg-gray-300 my-1" />
            {startSuggestions.map((suggestion, index) => (
              <DropdownMenu.Item
                key={index}
                className="flex items-center px-3 py-2 cursor-pointer hover:bg-gray-100"
                onClick={() => {
                  handleChange({
                    target: { id: "start", value: suggestion.description },
                  });
                  setStartSuggestions([]);
                }}
              >
                <MapPin className="mr-2 text-slate-600 w-5 h-5" />
                {suggestion.description}
              </DropdownMenu.Item>
            ))}
          </DropdownMenu.Content>
        </div>
      </DropdownMenu.Root>

      <DropdownMenu.Root open={restaurantSuggestions.length > 0}>
        <div className="flex justify-between my-6">
          <label className="p-2 formLabel" htmlFor="restaurant">
            <MapPin className="size-5 text-red-600" />
          </label>
          <input
            className="formInput"
            type="text"
            id="restaurant"
            value={formValues.restaurant || ""}
            onChange={handleRestaurantChange}
          />
          <DropdownMenu.Content className="bg-white border border-gray-300 rounded-md shadow-lg z-10 w-80 mt-2">
            {restaurantSuggestions.map((suggestion, index) => (
              <DropdownMenu.Item
                key={index}
                className="flex items-center px-3 py-2 cursor-pointer hover:bg-gray-100"
                onClick={() => {
                  handleChange({
                    target: { id: "restaurant", value: suggestion.description },
                  });
                  setRestaurantSuggestions([]);
                }}
              >
                <MapPin className="mr-2 text-slate-600 w-5 h-5" />
                {suggestion.description}
              </DropdownMenu.Item>
            ))}
          </DropdownMenu.Content>
        </div>
      </DropdownMenu.Root>

      <button
        onClick={onNext}
        type="submit"
        className="px-3 p-1 shadow-md hover:bg-sky-600/40 bg-sky-600/50 font-medium submit-btn"
      >
        Next
      </button>
    </form>
  );
};

export default LocationForm;
