import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import CarForm from './components/CarForm';
import LocationForm from './components/LocationForm';
import GasPriceForm from './components/GasPriceForm';

function App() {
  const [distance, setDistance] = useState(null);
  const [gasPrice, setGasPrice] = useState(null);
  const [fuelConsumption, setFuelConsumption] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [postalCode, setPostalCode] = useState(null);
  const [carFormValues, setCarFormValues] = useState({});
  const [locationFormValues, setLocationFormValues] = useState({});
  const [gasPriceFormValues, setGasPriceFormValues] = useState({});
  const [costToDrive, setCostToDrive] = useState(null);

  const handleCarFormChange = (e) => {
    setCarFormValues({ ...carFormValues, [e.target.id]: e.target.value });
  };

  const handleLocationFormChange = (e) => {
    setLocationFormValues({ ...locationFormValues, [e.target.id]: e.target.value });
  };

  const handleGasPriceFormChange = (e) => {
    setGasPriceFormValues({ ...gasPriceFormValues, [e.target.id]: Number(e.target.value) });
  };

  // Example functions: handleGetDistance, handleGetPostalCode, etc.

  return (
    <div className="bg-gray-900 min-h-screen flex flex-col items-center justify-center text-gray-100 p-6">
      <header className="mb-8">
        <h1 className="text-4xl font-bold text-white mb-4">Distance Cost Calculation</h1>
      </header>

      <div className="w-full max-w-lg space-y-6">
        <div className="bg-gray-800 rounded-lg p-6 shadow-md transition-all duration-500">
          <LocationForm
            formValues={locationFormValues}
            handleChange={handleLocationFormChange}
            handleGetDistance={() => {}}
            getUserLocation={() => {}}
          />
          {distance && <p className="text-xl mt-4">Distance: {distance} km</p>}
        </div>

        {distance && (
          <div className="bg-gray-800 rounded-lg p-6 shadow-md transition-opacity duration-500">
            <CarForm
              formValues={carFormValues}
              handleChange={handleCarFormChange}
              handleFuelConsumption={() => {}}
            />
            {fuelConsumption && (
              <p className="text-xl mt-4">Fuel consumption: {fuelConsumption} L/100km</p>
            )}
          </div>
        )}

        {fuelConsumption && (
          <div className="bg-gray-800 rounded-lg p-6 shadow-md transition-opacity duration-500">
            <GasPriceForm
              formValues={gasPriceFormValues}
              handleChange={handleGasPriceFormChange}
              fetchGasPrice={() => {}}
              enterGasPrice={() => {}}
            />
            {gasPrice && <p className="text-xl mt-4">Gas price: {gasPrice} c/L</p>}
          </div>
        )}

        {gasPrice && (
          <button
            onClick={() => {}}
            className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 rounded-md shadow-lg mt-4 transition-transform transform hover:scale-105"
          >
            Calculate cost for trip
          </button>
        )}

        {costToDrive && (
          <p className="text-xl mt-4 bg-gray-700 p-4 rounded-md">
            Cost to drive (roundtrip): ${costToDrive}
          </p>
        )}
      </div>
    </div>
  );
}

export default App;
