import React, {useState} from 'react'


const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const latitude = position.coords.latitude;
          const longitude = position.coords.longitude;
          const locationString = `${latitude}, ${longitude}`;
          console.log("Retrieved location:", locationString);
          return(locationString); // Update the state with user location
        },
        (error) => {
          console.log("Unable to retrieve your location.", error);
        }
      );
    } else {
      console.log("Geolocation is not supported by this browser.");
    }
};

  const DoSubmit = async() =>{
    document.getElementById("myinput").value = '1';
    return true;
  }
const LocationForm = ({ formValues, handleChange}) => {
    const [userLocation, setUserLocation] = useState("")
    return (
        <form onSubmit={()=> {}}>
            <div className='input-group'>
                <label htmlFor="start">Starting Point</label>
                <input
                  type="text"
                  id="start"
                  value={formValues.start || userLocation || " "}
                  onChange={handleChange}
                />
            </div>
            <button 
                type="button"
                onClick={()=>setUserLocation(getUserLocation())
                }
                >Use my location</button>
            <div className='input-group'>
                <label htmlFor="restaurant">Restaurant</label>
                <input
                  type="text"
                  id="restaurant"
                  value={formValues.restaurant || ""}
                  onChange={handleChange}
                />
            </div>
            <button type="submit" className = "submit-btn">Submit</button>
        </form>
    )
}

export default LocationForm
