import React from "react";
import { LocateFixed, Circle, MapPin } from "lucide-react";

const LocationForm = ({
  onNext,
  formValues,
  handleChange,
  handleGetDistance,
  getUserLocation,
}) => {
  return (
    <form
      onSubmit={handleGetDistance}
      className="w-96 p-6 px-10 rounded-lg bg-white shadow-lg mx-auto space-y-4 relative"
    >
      <h3 className="mb-7">Choose driving distance</h3>
      <div className="flex justify-between">
        <div className="flex items-center">
          <Circle className="size-4 text-blue-600" />
          <input
            className="formInput ml-2"
            type="text"
            id="start"
            placeholder="Enter starting location"
            value={formValues.start || ""}
            onChange={handleChange}
          />
          <button
            type="button"
            onClick={getUserLocation}
            className="ml-4 border border-black p-1 text-sm shadow-lg hover:bg-slate-200 flex items-center"
          >
            <LocateFixed className="mr-2 text-blue-600" />
            Your location
          </button>
        </div>
      </div>

      {/* Dotted line */}
      <div className="border-l-2 border-dotted border-gray-300 absolute left-1/2 top-12 bottom-12 -translate-x-1/2"></div>

      <div className="flex justify-between mt-6">
        <div className="flex items-center">
          <MapPin className="size-5 text-red-600" />
          <input
            className="formInput ml-2"
            type="text"
            id="restaurant"
            placeholder="Enter destination"
            value={formValues.restaurant || ""}
            onChange={handleChange}
          />
        </div>
      </div>
      <button
        onClick={onNext}
        type="submit"
        className="px-3 p-1 shadow-md hover:bg-sky-600/40 bg-sky-600/50 font-medium submit-btn mt-4"
      >
        Next
      </button>
    </form>
  );
};

export default LocationForm;
