import React from 'react';
import { LocateFixed, Circle, MapPin } from 'lucide-react';

const LocationForm = ({ formValues, handleChange, handleGetDistance, getUserLocation }) => {
    return (
        <form onSubmit={handleGetDistance} className="w-96 p-6 px-10 rounded-lg shadow-lg mx-auto space-y-4">
            <h3 className="mb-7">Choose driving distance</h3>
            <div className="">

                {/* Start Point */}
                <div className='flex items-center justify-between'>
                    <label className="formLabel p-1" htmlFor="start">
                        <Circle className="text-blue-600" />
                    </label>
                    <input
                        className="formInput shadow w-full ml-2"
                        type="text"
                        id="start"
                        value={formValues.start || ""}
                        onChange={handleChange}
                        placeholder="Your location"
                    />
                </div>

                {/* Get User Location Button */}
                <button 
                    type="button"
                    onClick={getUserLocation}
                    className="pr-2 pl-1 py-1 text-sm submit-btn shadow-lg hover:bg-white/100  bg-white/80 mt-1"
                >
                    <LocateFixed className="mr-2 text-blue-600" />
                    Use your location
                </button>

                {/* Divider with Dots */}
                <div className="flex justify-center my-4">
                    <div className="flex items-center space-x-1">
                        <Circle className="text-gray-400" size={6} />
                        <Circle className="text-gray-400" size={6} />
                        <Circle className="text-gray-400" size={6} />
                    </div>
                </div>

                {/* Destination Point */}
                <div className='flex items-center justify-between'>
                    <label className="formLabel" htmlFor="restaurant">
                        <MapPin className="text-red-600" />
                    </label>
                    <input
                        className="formInput shadow w-full ml-2"
                        type="text"
                        id="restaurant"
                        value={formValues.restaurant || ""}
                        onChange={handleChange}
                        placeholder="Destination address"
                    />
                </div>

            </div>

            {/* Submit Button */}
            <button type="submit" className="px-3 p-1 shadow-md hover:bg-blue-200 bg-blue-300 font-medium submit-btn mt-4">
                Enter
            </button>
        </form>
    );
};

export default LocationForm;
