import React, { useState } from 'react';

const getUserLocation = async (setFormValues, formValues) => {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(successFunction, errorFunction);
  } else {
    console.log("Geolocation is not supported by this browser.");
  }

  function successFunction(position) {
    const latitude = position.coords.latitude;
    const longitude = position.coords.longitude;
    const userLocation = `${latitude}, ${longitude}`;
    // Update the starting point field with the user's location
    setFormValues({
      ...formValues,
      start: userLocation
    });
  }

  function errorFunction() {
    console.log("Unable to retrieve your location.");
  }
};

const LocationForm = ({ formValues, handleChange, setFormValues }) => {
  return (
    <form onSubmit={() => {}}>
      <div className='input-group'>
        <label>Starting Point</label>
        <input
          type="text"
          id="start"
          value={formValues.start || ""}
          onChange={handleChange}
        />
      </div>
      <button 
        type="button" 
        onClick={() => getUserLocation(setFormValues, formValues)}
      >
        Use my location
      </button>
      <div className='input-group'>
        <label>Restaurant</label>
        <input
          type="text"
          id="restaurant"
          value={formValues.restaurant || ""}
          onChange={handleChange}
        />
      </div>
      <button type="submit" className="submit-btn">Submit</button>
    </form>
  );
};

export default LocationForm;
