import React from 'react';
import { LocateFixed } from 'lucide-react';

const LocationForm = ({ formValues, handleChange, handleGetDistance, getUserLocation }) => {
    return (
        <form 
          onSubmit={handleGetDistance} 
          className="bg-white/80 p-6 rounded-lg shadow-lg max-w-md mx-auto space-y-4"
        >
            <h3 className="mb-4 text-lg font-semibold text-center">Choose Driving Distance</h3>

            <div className="flex items-center space-x-2">
                <label className="text-gray-700 font-medium w-1/3" htmlFor="start">Starting Point:</label>
                <input
                  className="formInput flex-1 p-2 rounded-md border border-gray-300 focus:border-blue-400 focus:ring focus:ring-blue-200 focus:ring-opacity-50 shadow"
                  type="text"
                  id="start"
                  value={formValues.start || ""}
                  onChange={handleChange}
                />
            </div>

            <button
                type="button"
                onClick={getUserLocation}
                className="flex items-center px-3 py-2 text-sm font-medium text-blue-600 bg-white border border-blue-600 rounded-md hover:bg-blue-100 transition duration-200"
            >
                <LocateFixed className="w-4 h-4 mr-2" /> Your location
            </button>

            <div className="flex items-center space-x-2">
                <label className="text-gray-700 font-medium w-1/3" htmlFor="restaurant">Destination:</label>
                <input
                  className="formInput flex-1 p-2 rounded-md border border-gray-300 focus:border-blue-400 focus:ring focus:ring-blue-200 focus:ring-opacity-50 shadow"
                  type="text"
                  id="restaurant"
                  value={formValues.restaurant || ""}
                  onChange={handleChange}
                />
            </div>

            <button 
              type="submit" 
              className="w-full px-4 py-2 mt-4 font-semibold text-white bg-blue-500 rounded-md shadow hover:bg-blue-600 transition duration-200"
            >
              Enter
            </button>
        </form>
    );
}

export default LocationForm;
