import React, {useState} from 'react'

const getUserLocation = (setUserLocation) => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const latitude = position.coords.latitude;
          const longitude = position.coords.longitude;
          const locationString = `${latitude}, ${longitude}`
          setUserLocation({latitude, longitude}); // Update the state with user location
          console.log(userLocation)
        },
        (error) => {
          console.log("Unable to retrieve your location.", error)
        }
      )
    } else {
      console.log("Geolocation is not supported by this browser.")
    }
}

const LocationForm = ({ formValues, handleChange, handleGetDistance, handleGetPostalCode}) => {
    const [userLocation, setUserLocation] = useState("")
    return (
        <form onSubmit={()=> {handleGetDistance(); handleGetPostalCode()}}>
            <div className='input-group'>
                <label htmlFor="start">Starting Point</label>
                <input
                  type="text"
                  id="start"
                  value={formValues.start || userLocation || " "}
                  onChange={handleChange}
                />
            </div>
            <button 
                type="button"
                onClick={()=>getUserLocation(setUserLocation)
                }
                >Use my location</button>
            <div className='input-group'>
                <label htmlFor="restaurant">Restaurant</label>
                <input
                  type="text"
                  id="restaurant"
                  value={formValues.restaurant || ""}
                  onChange={handleChange}
                />
            </div>
            <button type="submit" className = "submit-btn">Submit</button>
        </form>
    )
}

export default LocationForm
